# Function to fetch and parse the Steam page
def fetch_steam_review_percentage(url):
    try:
        response = requests.get(url)
        if response.status_code == 200:
            soup = BeautifulSoup(response.content, 'html.parser')
            # Look for the review percentage element
            review_element = soup.find('div', class_='user_reviews')
            if review_element:
                # Extract the review percentage
                review_text = review_element.get_text(strip=True)
                # Look for the percentage in the text
                for line in review_text.splitlines():
                    if '%' in line:
                        percentage = line.split('%')[0].strip()
                        if percentage.isdigit():
                            return float(percentage)
                return 0.0
            else:
                return 0.0
        else:
            return 0.0
    except Exception as e:
        return 0.0

